import React, { useEffect, useState } from 'react';
import Tab from './Tab';
import Button from '@mui/material/Button';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const InventoryPage = () => {
    const navigate = useNavigate();
    const [items, setItems] = useState([]);

    const redirectToDetection = () => {
        navigate('/detection');
    };

    const fetchItems = async () => {
        try {
            const result = await axios.get("https://digitward.onrender.com/items/fetch");
            return result.data;
        } catch (error) {
            console.error("Error fetching items:", error);
            return [];
        }
    };

    const elementedItems = async () => {
        const itemsData = await fetchItems();
        return itemsData.map((item) => (
            <div key={item.id}>
                <Tab name={item.name} imageSrc={item.ImageSource} />
            </div>
        ));
    };

    useEffect(() => {
        const fetchData = async () => {
            const result = await elementedItems();
            setItems(result);
        };

        fetchData();
    }, []);

    return (
        <div style={{
            display: "flex",
            gap: "20px",
            flexDirection: "column"
        }}>
            <Button variant="contained" onClick={redirectToDetection}>Get Recommendations</Button>
            <Button variant="contained" onClick={redirectToDetection}>Add item</Button>
            {items}
        </div>
    );
};

export default InventoryPage;
