import React, { useEffect, useState } from "react";
import Tab from "./Tab";
import Button from "@mui/material/Button";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const InventoryPage = () => {
  const navigate = useNavigate();
  const [items, setItems] = useState([]);

  const redirectToDetection = () => {
    navigate("/detection");
  };

  const fetchItems = async () => {
    try {
      const result = await axios.get(
        "https://digitward.onrender.com/items/fetch"
      );
      return result.data;
    } catch (error) {
      console.error("Error fetching items:", error);
      return [];
    }
  };

  const elementedItems = async () => {
    const itemsData = await fetchItems();
    return itemsData.map((item) => (
      <div key={item.id}>
        <Tab name={item.name} imageSrc={item.ImageSource} />
      </div>
    ));
  };

  useEffect(() => {
    const fetchData = async () => {
      const result = await elementedItems();
      setItems(result);
    };

    fetchData();
  }, []);

  return (
    <div
      style={{
        border: "1px solid #ddd",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        padding: "20px",
        maxWidth: "800px",
        margin: "0 auto",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
        Inventory Page
      </h2>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          gap: "20px",
        }}
      >
        <Button
          variant="contained"
          color="primary"
          onClick={redirectToDetection}
          style={{ width: "100%", marginBottom: "20px" }}
        >
          Get Recommendations
        </Button>
        <Button
          variant="contained"
          color="secondary"
          onClick={redirectToDetection}
          style={{
            width: "100%",
            marginBottom: "20px",
            color:
              "linear-gradient(to right, rgb(255, 255, 255), rgb(133, 180, 255));",
          }}
        >
          Add Item
        </Button>
        {items.length > 0 ? (
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))",
              gap: "16px",
            }}
          >
            {items}
          </div>
        ) : (
          <p>No items available</p>
        )}
      </div>
    </div>
  );
};

export default InventoryPage;
